(()=>{const e=document.getElementById("text"),t=document.getElementById("submit"),n=document.getElementById("form"),i=document.getElementById("weather-description"),d=document.getElementById("city"),m=document.getElementById("temperature"),a=document.getElementById("feels-like"),c=document.getElementById("wind"),o=document.getElementById("humidity");async function r(e){const t=await fetch(`http://api.openweathermap.org/data/2.5/weather?q=${e},&APPID=517bdda0ae62f37b25ca4c3e3a3cc910&units=metric`,{mode:"cors"}),n=await t.json();console.log(n);const r=n.weather[0].description.toUpperCase(),s=n.name.toUpperCase(),{country:u}=n.sys,{temp:l}=n.main,y=n.main.feels_like,p=n.wind.speed,{humidity:g}=n.main;i.innerHTML=r,d.innerHTML=`${s}, ${u}`,m.innerHTML=`${l}°C`,a.innerHTML=`Feels like: ${y}°C`,c.innerHTML=`Wind: ${p} Km/h`,o.innerHTML=`Humidity: ${g}%`}function s(t){t.preventDefault(),r(e.value)}t.addEventListener("click",s),n.addEventListener("submit",s),r("Pescantina")})();